import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { tournamentAPI, matchesAPI } from '../services/api';

const Tournament: React.FC = () => {
  const [tournament, setTournament] = useState<any>(null);
  const [matches, setMatches] = useState<any[]>([]);
  const [goalScorers, setGoalScorers] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTournamentData();
  }, []);

  const fetchTournamentData = async () => {
    try {
      const [tournamentRes, matchesRes, scorersRes] = await Promise.all([
        tournamentAPI.getCurrent(),
        matchesAPI.getAll(),
        matchesAPI.getGoalScorers()
      ]);

      setTournament(tournamentRes.data);
      setMatches(matchesRes.data);
      setGoalScorers(scorersRes.data);
    } catch (error) {
      console.error('Error fetching tournament data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getRoundMatches = (round: string) => {
    return matches.filter(match => match.round === round && match.tournamentId === tournament?.id);
  };

  const renderBracket = () => {
    if (!tournament) return null;

    const quarters = getRoundMatches('quarterfinal');
    const semis = getRoundMatches('semifinal');
    const final = getRoundMatches('final')[0];

    // Organize quarter-finals into left and right sides
    const leftQuarters = quarters.slice(0, 2); // First two quarter-finals
    const rightQuarters = quarters.slice(2, 4); // Last two quarter-finals

    // Organize semi-finals
    const leftSemi = semis.find(match => 
      leftQuarters.some(q => q.teamAId === match.teamAId || q.teamBId === match.teamAId || 
                           q.teamAId === match.teamBId || q.teamBId === match.teamBId)
    );
    const rightSemi = semis.find(match => 
      rightQuarters.some(q => q.teamAId === match.teamAId || q.teamBId === match.teamAId || 
                            q.teamAId === match.teamBId || q.teamBId === match.teamBId)
    );

    return (
      <div className="tournament-bracket p-8 rounded-lg">
        <div className="flex justify-between items-stretch min-h-[600px]">
          {/* Left Side - Quarter Finals and Semi Final */}
          <div className="w-2/5 flex flex-col justify-between">
            {/* Left Quarter Finals */}
            <div className="space-y-8">
              {leftQuarters.map((match, index) => (
                <div key={match.id} className="text-right">
                  <MatchCard match={match} side="left" />
                </div>
              ))}
            </div>

            {/* Left Semi Final */}
            <div className="text-right">
              {leftSemi && <MatchCard match={leftSemi} side="left" />}
            </div>
          </div>

          {/* Center - Trophy and Final */}
          <div className="w-1/5 flex flex-col items-center justify-center relative">
            {/* Connecting lines */}
            <div className="absolute top-0 bottom-0 w-px bg-white/30"></div>

            {/* Trophy */}
            <div className="bg-yellow-500 rounded-full p-6 mb-8 shadow-2xl border-4 border-yellow-300">
              <div className="text-4xl">üèÜ</div>
            </div>

            {/* Final Match */}
            <div className="w-full">
              {final && <MatchCard match={final} side="center" />}
            </div>

            {tournament.winner && (
              <div className="bg-yellow-500 text-white p-4 rounded-lg mt-4 text-center shadow-lg">
                <div className="text-lg font-bold">CHAMPION</div>
                <div className="text-xl font-bold">{tournament.winner}</div>
              </div>
            )}
          </div>

          {/* Right Side - Quarter Finals and Semi Final */}
          <div className="w-2/5 flex flex-col justify-between">
            {/* Right Quarter Finals */}
            <div className="space-y-8">
              {rightQuarters.map((match, index) => (
                <div key={match.id}>
                  <MatchCard match={match} side="right" />
                </div>
              ))}
            </div>

            {/* Right Semi Final */}
            <div>
              {rightSemi && <MatchCard match={rightSemi} side="right" />}
            </div>
          </div>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-center items-center h-64">
          <div className="text-xl">Loading tournament data...</div>
        </div>
      </div>
    );
  }

  if (!tournament) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <h2 className="text-3xl font-bold text-gray-800 mb-4">No Active Tournament</h2>
          <p className="text-gray-600 mb-8">
            There is no active tournament currently. An administrator can start a tournament when 8 teams are registered.
          </p>
          <Link
            to="/admin"
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg font-semibold"
          >
            Go to Admin Panel
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold text-gray-800 mb-2">African Nations League</h1>
        <p className="text-gray-600">Tournament Bracket - {tournament.currentRound}</p>
      </div>

      {renderBracket()}

      {/* Goal Scorers Leaderboard */}
      <div className="bg-white rounded-lg shadow mt-8">
        <div className="px-6 py-4 border-b">
          <h2 className="text-2xl font-semibold text-gray-800">Goal Scorers Leaderboard</h2>
        </div>
        <div className="p-6">
          {goalScorers.length === 0 ? (
            <div className="text-center text-gray-500 py-4">
              No goals scored yet in the tournament.
            </div>
          ) : (
            <div className="space-y-2">
              {goalScorers.map((scorer, index) => (
                <div key={scorer.playerId} className="flex items-center justify-between p-3 bg-gray-50 rounded">
                  <div className="flex items-center space-x-4">
                    <div className="w-8 h-8 bg-blue-500 text-white rounded-full flex items-center justify-center font-bold">
                      {index + 1}
                    </div>
                    <div>
                      <div className="font-semibold">{scorer.playerName}</div>
                    </div>
                  </div>
                  <div className="text-lg font-bold text-blue-600">
                    {scorer.goals} {scorer.goals === 1 ? 'goal' : 'goals'}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const MatchCard: React.FC<{ match: any; side: 'left' | 'right' | 'center' }> = ({ match, side }) => {
  const isCompleted = match.status === 'completed';

  const getCardAlignment = () => {
    switch (side) {
      case 'left':
        return 'text-right';
      case 'right':
        return 'text-left';
      case 'center':
        return 'text-center';
      default:
        return '';
    }
  };

  const getTeamOrder = () => {
    if (side === 'left') {
      return { firstTeam: match.teamBName, secondTeam: match.teamAName };
    } else {
      return { firstTeam: match.teamAName, secondTeam: match.teamBName };
    }
  };

  const { firstTeam, secondTeam } = getTeamOrder();

  return (
    <div className={`bg-white rounded-lg shadow-lg p-4 hover:shadow-xl transition-all duration-300 border-2 ${
      match.round === 'final' ? 'border-yellow-400' : 
      match.round === 'semifinal' ? 'border-purple-400' : 
      'border-blue-400'
    } ${getCardAlignment()}`}>
      <Link to={`/match/${match.id}`}>
        <div className="text-center mb-3">
          <span className={`px-3 py-1 rounded-full text-xs font-semibold ${
            match.round === 'final' ? 'bg-yellow-500 text-white' :
            match.round === 'semifinal' ? 'bg-purple-500 text-white' :
            'bg-blue-500 text-white'
          }`}>
            {match.round.toUpperCase()}
          </span>
          {match.matchType === 'played' && (
            <span className="ml-2 px-2 py-1 bg-green-500 text-white rounded-full text-xs">AI PLAYED</span>
          )}
        </div>

        <div className="space-y-2">
          <div className={`flex items-center justify-between ${side === 'center' ? 'flex-col space-y-2' : ''}`}>
            {side === 'center' ? (
              <>
                <div className="font-semibold text-lg">{firstTeam}</div>
                <div className="flex items-center space-x-3">
                  {isCompleted ? (
                    <>
                      <div className="text-xl font-bold bg-gray-100 px-3 py-1 rounded">
                        {side === 'left' ? match.scoreB : match.scoreA}
                      </div>
                      <div className="text-gray-400 font-bold">-</div>
                      <div className="text-xl font-bold bg-gray-100 px-3 py-1 rounded">
                        {side === 'left' ? match.scoreA : match.scoreB}
                      </div>
                    </>
                  ) : (
                    <div className="text-sm text-gray-500 bg-gray-100 px-3 py-1 rounded">VS</div>
                  )}
                </div>
                <div className="font-semibold text-lg">{secondTeam}</div>
              </>
            ) : (
              <>
                <div className={`font-semibold ${side === 'left' ? 'order-2' : 'order-1'}`}>
                  {firstTeam}
                </div>
                <div className={`flex items-center space-x-2 ${side === 'left' ? 'order-1' : 'order-2'}`}>
                  {isCompleted ? (
                    <>
                      <div className="text-lg font-bold bg-gray-100 px-2 py-1 rounded min-w-8 text-center">
                        {side === 'left' ? match.scoreB : match.scoreA}
                      </div>
                      <div className="text-gray-400">-</div>
                      <div className="text-lg font-bold bg-gray-100 px-2 py-1 rounded min-w-8 text-center">
                        {side === 'left' ? match.scoreA : match.scoreB}
                      </div>
                    </>
                  ) : (
                    <div className="text-sm text-gray-500 bg-gray-100 px-2 py-1 rounded">VS</div>
                  )}
                </div>
                <div className={`font-semibold ${side === 'left' ? 'order-3' : 'order-3'}`}>
                  {secondTeam}
                </div>
              </>
            )}
          </div>
        </div>

        {isCompleted && match.goalScorers.length > 0 && (
          <div className="mt-3 text-xs text-gray-600 border-t pt-2">
            <div className="font-semibold mb-1">Goals:</div>
            {match.goalScorers.slice(0, 2).map((scorer: any) => (
              <div key={`${scorer.playerId}-${scorer.minute}`} className="truncate">
                {scorer.minute}' {scorer.playerName}
              </div>
            ))}
            {match.goalScorers.length > 2 && (
              <div className="text-blue-600">+{match.goalScorers.length - 2} more</div>
            )}
          </div>
        )}
      </Link>
    </div>
  );
};

export default Tournament;
