// frontend/src/context/TournamentContext.tsx
import React, { createContext, useContext, useState, useEffect } from 'react';
import { tournamentAPI } from '../services/api';

const TournamentContext = createContext<any>(null);

export const TournamentProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [tournament, setTournament] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  const fetchTournament = async () => {
    try {
      const response = await tournamentAPI.getCurrent();
      setTournament(response.data);
    } catch (error) {
      console.error('Error fetching tournament:', error);
      setTournament(null);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTournament();

    // Auto-refresh every 10 seconds
    const interval = setInterval(fetchTournament, 10000);
    return () => clearInterval(interval);
  }, []);

  return (
    <TournamentContext.Provider value={{ tournament, loading, refreshTournament: fetchTournament }}>
      {children}
    </TournamentContext.Provider>
  );
};

export const useTournament = () => useContext(TournamentContext);
