import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { tournamentAPI, matchesAPI } from '../services/api';

const Tournament: React.FC = () => {
  const [tournament, setTournament] = useState<any>(null);
  const [matches, setMatches] = useState<any[]>([]);
  const [goalScorers, setGoalScorers] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTournamentData();
  }, []);

  const fetchTournamentData = async () => {
    try {
      const [tournamentRes, matchesRes, scorersRes] = await Promise.all([
        tournamentAPI.getCurrent(),
        matchesAPI.getAll(),
        matchesAPI.getGoalScorers()
      ]);

      setTournament(tournamentRes.data);
      setMatches(matchesRes.data);
      setGoalScorers(scorersRes.data);
    } catch (error) {
      console.error('Error fetching tournament data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getRoundMatches = (round: string) => {
    return matches.filter(match => match.round === round && match.tournamentId === tournament?.id);
  };

  const renderBracket = () => {
    if (!tournament) return null;

    const quarters = getRoundMatches('quarterfinal');
    const semis = getRoundMatches('semifinal');
    const final = getRoundMatches('final')[0];

    return (
      <div className="tournament-bracket">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="text-3xl font-bold text-white mb-2">Nigeria</div>
          <div className="text-5xl font-bold text-yellow-400 mb-2">CARICA CUP OF NATIONS</div>
          <div className="text-2xl font-bold text-white">C√îTE D'IVOIRE 23</div>
        </div>

        <div className="flex justify-between items-start max-w-6xl mx-auto">
          {/* Quarter Finals - Left Side */}
          <div className="w-1/4">
            <div className="space-y-8">
              {quarters.slice(0, 2).map((match, index) => (
                <MatchCard key={match.id} match={match} />
              ))}
            </div>
          </div>

          {/* Quarter Finals - Right Side */}
          <div className="w-1/4">
            <div className="space-y-8">
              {quarters.slice(2, 4).map((match, index) => (
                <MatchCard key={match.id} match={match} />
              ))}
            </div>
          </div>

          {/* Semi Finals */}
          <div className="w-1/4 mt-16">
            <div className="space-y-20">
              {semis.map((match, index) => (
                <MatchCard key={match.id} match={match} />
              ))}
            </div>
          </div>

          {/* Final */}
          <div className="w-1/4 mt-32">
            <div className="text-center mb-4">
              <div className="text-2xl font-bold text-yellow-400 mb-2">FINAL</div>
              <div className="text-white font-semibold">Sunday, 11 February</div>
            </div>
            {final && <MatchCard key={final.id} match={final} isFinal={true} />}

            {tournament.winner && (
              <div className="bg-gradient-to-r from-yellow-400 to-yellow-600 text-white p-6 rounded-lg mt-6 text-center shadow-lg">
                <div className="text-2xl font-bold mb-2">üèÜ Champion</div>
                <div className="text-3xl font-bold">{tournament.winner}</div>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-900 to-green-700 flex items-center justify-center">
        <div className="text-xl text-white">Loading tournament data...</div>
      </div>
    );
  }

  if (!tournament) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-900 to-green-700 flex items-center justify-center">
        <div className="text-center text-white">
          <h2 className="text-3xl font-bold mb-4">No Active Tournament</h2>
          <p className="text-gray-200 mb-8">
            There is no active tournament currently. An administrator can start a tournament when 8 teams are registered.
          </p>
          <Link
            to="/admin"
            className="bg-yellow-500 hover:bg-yellow-600 text-white px-6 py-3 rounded-lg font-semibold"
          >
            Go to Admin Panel
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-900 to-green-700 py-8">
      {renderBracket()}

      {/* Goal Scorers Leaderboard */}
      <div className="max-w-4xl mx-auto mt-16 bg-white/10 backdrop-blur-sm rounded-xl p-8">
        <div className="text-center mb-6">
          <h2 className="text-3xl font-bold text-yellow-400">Magic</h2>
          <div className="text-white text-lg mt-2">Goal Scorers Leaderboard</div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {goalScorers.length === 0 ? (
            <div className="col-span-4 text-center text-gray-300 py-8">
              No goals scored yet in the tournament.
            </div>
          ) : (
            goalScorers.map((scorer, index) => (
              <div key={scorer.playerId} className="bg-white/20 backdrop-blur-sm rounded-lg p-4 text-center">
                <div className="text-2xl font-bold text-yellow-400 mb-2">{scorer.goals}</div>
                <div className="text-white font-semibold">{scorer.playerName}</div>
                <div className="text-gray-300 text-sm mt-1">
                  {scorer.goals === 1 ? 'goal' : 'goals'}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

const MatchCard: React.FC<{ match: any; isFinal?: boolean }> = ({ match, isFinal = false }) => {
  const isCompleted = match.status === 'completed';

  return (
    <div className={`bg-white/20 backdrop-blur-sm rounded-xl p-4 border-2 ${
      isFinal ? 'border-yellow-400' : 'border-white/30'
    } hover:bg-white/30 transition-all duration-300`}>
      <Link to={`/match/${match.id}`}>
        <div className="text-center mb-3">
          {!isFinal && (
            <span className="text-white text-sm font-semibold bg-white/20 px-3 py-1 rounded-full">
              {match.round === 'semifinal' ? 'Semi Final' : 'Quarter Final'}
            </span>
          )}
        </div>

        <div className="space-y-3">
          <div className="flex justify-between items-center">
            <div className="text-white font-bold text-lg flex-1 text-right pr-3">
              {match.teamAName}
            </div>

            <div className="flex items-center space-x-3">
              {isCompleted ? (
                <>
                  <div className={`text-xl font-bold ${
                    match.scoreA > match.scoreB ? 'text-yellow-400' : 'text-white'
                  }`}>
                    {match.scoreA}
                  </div>
                  <div className="text-white">-</div>
                  <div className={`text-xl font-bold ${
                    match.scoreB > match.scoreA ? 'text-yellow-400' : 'text-white'
                  }`}>
                    {match.scoreB}
                  </div>
                </>
              ) : (
                <div className="text-white font-semibold">VS</div>
              )}
            </div>

            <div className="text-white font-bold text-lg flex-1 pl-3">
              {match.teamBName}
            </div>
          </div>

          {isCompleted && match.goalScorers.length > 0 && (
            <div className="mt-3 pt-3 border-t border-white/20">
              <div className="text-yellow-300 text-sm text-center">
                {match.goalScorers.slice(0, 2).map((scorer: any) => (
                  <div key={`${scorer.playerId}-${scorer.minute}`}>
                    {scorer.minute}' {scorer.playerName}
                  </div>
                ))}
                {match.goalScorers.length > 2 && (
                  <div className="text-white/70">+{match.goalScorers.length - 2} more</div>
                )}
              </div>
            </div>
          )}
        </div>
      </Link>
    </div>
  );
};

export default Tournament;
