import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { tournamentAPI, matchesAPI } from '../services/api';

const Tournament: React.FC = () => {
  const [tournament, setTournament] = useState<any>(null);
  const [matches, setMatches] = useState<any[]>([]);
  const [goalScorers, setGoalScorers] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTournamentData();
  }, []);

  const fetchTournamentData = async () => {
    try {
      const [tournamentRes, matchesRes, scorersRes] = await Promise.all([
        tournamentAPI.getCurrent(),
        matchesAPI.getAll(),
        matchesAPI.getGoalScorers()
      ]);

      setTournament(tournamentRes.data);
      setMatches(matchesRes.data);
      setGoalScorers(scorersRes.data);
    } catch (error) {
      console.error('Error fetching tournament data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getRoundMatches = (round: string) => {
    return matches.filter(match => match.round === round && match.tournamentId === tournament?.id);
  };

  // Get country flag emoji
  const getCountryFlag = (countryName: string) => {
    const flagMap: { [key: string]: string } = {
      'Nigeria': 'üá≥üá¨',
      'Egypt': 'üá™üá¨',
      'South Africa': 'üáøüá¶',
      'Ghana': 'üá¨üá≠',
      'Morocco': 'üá≤üá¶',
      'Senegal': 'üá∏üá≥',
      'Ivory Coast': 'üá®üáÆ',
      'Cameroon': 'üá®üá≤',
      'Algeria': 'üá©üáø',
      'Tunisia': 'üáπüá≥',
      'Kenya': 'üá∞üá™',
      'Ethiopia': 'üá™üáπ',
      'DR Congo': 'üá®üá©',
      'Tanzania': 'üáπüáø',
      'Uganda': 'üá∫üá¨',
      'Zambia': 'üáøüá≤',
      'Zimbabwe': 'üáøüáº',
      'Mali': 'üá≤üá±',
      'Burkina Faso': 'üáßüá´',
      'Angola': 'üá¶üá¥',
      'Cape Verde': 'üá®üáª',
      'Guinea': 'üá¨üá≥'
    };
    return flagMap[countryName] || 'üè¥';
  };

  const getWinningTeam = (match: any) => {
    if (match.status !== 'completed') return null;
    if (match.scoreA > match.scoreB) return match.teamAId;
    if (match.scoreB > match.scoreA) return match.teamBId;
    return null; // Draw or not completed
  };

  const renderBracket = () => {
    if (!tournament) return null;

    const quarters = getRoundMatches('quarterfinal');
    const semis = getRoundMatches('semifinal');
    const final = getRoundMatches('final')[0];

    // Organize matches by position in bracket
    const qf1 = quarters[0]; // Top left quarter
    const qf2 = quarters[1]; // Bottom left quarter
    const qf3 = quarters[2]; // Top right quarter
    const qf4 = quarters[3]; // Bottom right quarter

    const sf1 = semis[0]; // Left semi
    const sf2 = semis[1]; // Right semi

    return (
      <div className="tournament-bracket p-8 rounded-lg">
        <div className="flex justify-between items-stretch min-h-[800px] relative">

          {/* Left Side - Quarter Finals and Semi Final */}
          <div className="w-2/5 flex flex-col justify-between py-8">

            {/* Quarter Final 1 (Top Left) */}
            <div className="relative">
              <div className="flex items-center justify-end space-x-4 mb-8">
                <div className="text-right">
                  <div className="font-semibold text-white mb-1">Quarter Final 1</div>
                  <MatchTeams match={qf1} position="left" />
                </div>
              </div>
              {/* Connector to Semi Final */}
              <div className="absolute right-0 top-1/2 w-16 h-px bg-white/50"></div>
              <div className="absolute right-16 top-1/2 w-px h-32 bg-white/50 -translate-y-1/2"></div>
            </div>

            {/* Quarter Final 2 (Bottom Left) */}
            <div className="relative">
              <div className="flex items-center justify-end space-x-4 mt-8">
                <div className="text-right">
                  <div className="font-semibold text-white mb-1">Quarter Final 2</div>
                  <MatchTeams match={qf2} position="left" />
                </div>
              </div>
              {/* Connector to Semi Final */}
              <div className="absolute right-0 top-1/2 w-16 h-px bg-white/50"></div>
              <div className="absolute right-16 top-1/2 w-px h-32 bg-white/50 translate-y-1/2"></div>
            </div>

            {/* Left Semi Final */}
            <div className="relative flex justify-end mt-16">
              <div className="text-right mr-16">
                <div className="font-semibold text-white mb-1">Semi Final</div>
                <MatchTeams match={sf1} position="left" />
              </div>
              {/* Connector to Final */}
              <div className="absolute right-0 top-1/2 w-32 h-px bg-white/50"></div>
            </div>

          </div>

          {/* Center - Trophy and Final */}
          <div className="w-1/5 flex flex-col items-center justify-center relative">

            {/* Vertical center line */}
            <div className="absolute top-0 bottom-0 w-px bg-white/30"></div>

            {/* Final Match */}
            <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-6 border-2 border-yellow-400 shadow-2xl">
              <div className="text-center mb-4">
                <span className="bg-yellow-500 text-white px-4 py-1 rounded-full text-sm font-bold">
                  FINAL
                </span>
              </div>
              <MatchTeams match={final} position="center" />
            </div>

            {/* Trophy */}
            <div className="trophy-glow mt-8">
              <div className="bg-yellow-500 rounded-full p-8 shadow-2xl border-4 border-yellow-300">
                <div className="text-6xl">üèÜ</div>
              </div>
            </div>

            {tournament.winner && (
              <div className="bg-yellow-500 text-white p-4 rounded-lg mt-4 text-center shadow-lg">
                <div className="text-lg font-bold">CHAMPION</div>
                <div className="text-xl font-bold">{tournament.winner}</div>
              </div>
            )}

          </div>

          {/* Right Side - Quarter Finals and Semi Final */}
          <div className="w-2/5 flex flex-col justify-between py-8">

            {/* Quarter Final 3 (Top Right) */}
            <div className="relative">
              <div className="flex items-center justify-start space-x-4 mb-8">
                <div className="text-left">
                  <div className="font-semibold text-white mb-1">Quarter Final 3</div>
                  <MatchTeams match={qf3} position="right" />
                </div>
              </div>
              {/* Connector to Semi Final */}
              <div className="absolute left-0 top-1/2 w-16 h-px bg-white/50"></div>
              <div className="absolute left-16 top-1/2 w-px h-32 bg-white/50 -translate-y-1/2"></div>
            </div>

            {/* Quarter Final 4 (Bottom Right) */}
            <div className="relative">
              <div className="flex items-center justify-start space-x-4 mt-8">
                <div className="text-left">
                  <div className="font-semibold text-white mb-1">Quarter Final 4</div>
                  <MatchTeams match={qf4} position="right" />
                </div>
              </div>
              {/* Connector to Semi Final */}
              <div className="absolute left-0 top-1/2 w-16 h-px bg-white/50"></div>
              <div className="absolute left-16 top-1/2 w-px h-32 bg-white/50 translate-y-1/2"></div>
            </div>

            {/* Right Semi Final */}
            <div className="relative flex justify-start mt-16">
              <div className="text-left ml-16">
                <div className="font-semibold text-white mb-1">Semi Final</div>
                <MatchTeams match={sf2} position="right" />
              </div>
              {/* Connector to Final */}
              <div className="absolute left-0 top-1/2 w-32 h-px bg-white/50"></div>
            </div>

          </div>

        </div>
      </div>
    );
  };

  const MatchTeams: React.FC<{ match: any; position: 'left' | 'right' | 'center' }> = ({ match, position }) => {
    if (!match) {
      return (
        <div className="space-y-4">
          <div className="flex items-center space-x-3 justify-center">
            <div className="w-16 h-16 bg-gray-300 rounded-full flex items-center justify-center">
              <span className="text-2xl">?</span>
            </div>
          </div>
          <div className="flex items-center space-x-3 justify-center">
            <div className="w-16 h-16 bg-gray-300 rounded-full flex items-center justify-center">
              <span className="text-2xl">?</span>
            </div>
          </div>
        </div>
      );
    }

    const isCompleted = match.status === 'completed';
    const winner = getWinningTeam(match);

    const TeamCircle: React.FC<{ teamName: string; isWinner: boolean; isLeft?: boolean }> = ({ 
      teamName, 
      isWinner, 
      isLeft = false 
    }) => (
      <Link to={`/match/${match.id}`} className="block">
        <div className={`flex flex-col items-center space-y-2 ${
          position === 'center' ? 'mx-2' : ''
        }`}>
          <div className={`relative group cursor-pointer ${
            isWinner ? 'scale-110 transform transition-transform' : ''
          }`}>
            <div className={`w-20 h-20 rounded-full border-4 flex items-center justify-center text-3xl shadow-lg transition-all duration-300 ${
              isWinner 
                ? 'border-yellow-400 bg-yellow-100 shadow-yellow-200' 
                : 'border-white bg-white/20 shadow-white/30'
            } group-hover:scale-105 group-hover:shadow-xl`}>
              {getCountryFlag(teamName)}
            </div>
            {isCompleted && (
              <div className={`absolute -top-1 -right-1 w-6 h-6 rounded-full flex items-center justify-center text-xs font-bold ${
                isWinner ? 'bg-green-500 text-white' : 'bg-red-500 text-white'
              }`}>
                {isWinner ? 'W' : 'L'}
              </div>
            )}
          </div>
          <div className={`text-center max-w-24 ${
            isWinner ? 'font-bold text-yellow-300' : 'text-white'
          }`}>
            <div className="text-sm leading-tight">{teamName}</div>
            {isCompleted && (
              <div className="text-xs opacity-75">
                {match.matchType === 'played' ? 'AI' : 'SIM'}
              </div>
            )}
          </div>
        </div>
      </Link>
    );

    const ScoreDisplay: React.FC<{ match: any }> = ({ match }) => (
      <div className="flex items-center justify-center space-x-2 my-2">
        <div className="bg-white/20 rounded px-2 py-1 text-white font-bold min-w-8 text-center">
          {match.scoreA}
        </div>
        <div className="text-white font-bold">-</div>
        <div className="bg-white/20 rounded px-2 py-1 text-white font-bold min-w-8 text-center">
          {match.scoreB}
        </div>
      </div>
    );

    if (position === 'center') {
      return (
        <div className="space-y-4">
          <TeamCircle 
            teamName={match.teamAName} 
            isWinner={winner === match.teamAId}
          />
          {isCompleted && <ScoreDisplay match={match} />}
          <TeamCircle 
            teamName={match.teamBName} 
            isWinner={winner === match.teamBId}
          />
        </div>
      );
    }

    if (position === 'left') {
      return (
        <div className="space-y-6">
          <TeamCircle 
            teamName={match.teamBName} 
            isWinner={winner === match.teamBId}
          />
          {isCompleted && <ScoreDisplay match={match} />}
          <TeamCircle 
            teamName={match.teamAName} 
            isWinner={winner === match.teamAId}
          />
        </div>
      );
    }

    // position === 'right'
    return (
      <div className="space-y-6">
        <TeamCircle 
          teamName={match.teamAName} 
          isWinner={winner === match.teamAId}
        />
        {isCompleted && <ScoreDisplay match={match} />}
        <TeamCircle 
          teamName={match.teamBName} 
          isWinner={winner === match.teamBId}
        />
      </div>
    );
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-center items-center h-64">
          <div className="text-xl">Loading tournament data...</div>
        </div>
      </div>
    );
  }

  if (!tournament) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <h2 className="text-3xl font-bold text-gray-800 mb-4">No Active Tournament</h2>
          <p className="text-gray-600 mb-8">
            There is no active tournament currently. An administrator can start a tournament when 8 teams are registered.
          </p>
          <Link
            to="/admin"
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg font-semibold"
          >
            Go to Admin Panel
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold text-gray-800 mb-2">African Nations League</h1>
        <p className="text-gray-600">Tournament Bracket - {tournament.currentRound}</p>
      </div>

      {renderBracket()}

      {/* Goal Scorers Leaderboard */}
      <div className="bg-white rounded-lg shadow mt-8">
        <div className="px-6 py-4 border-b">
          <h2 className="text-2xl font-semibold text-gray-800">Goal Scorers Leaderboard</h2>
        </div>
        <div className="p-6">
          {goalScorers.length === 0 ? (
            <div className="text-center text-gray-500 py-4">
              No goals scored yet in the tournament.
            </div>
          ) : (
            <div className="space-y-2">
              {goalScorers.map((scorer, index) => (
                <div key={scorer.playerId} className="flex items-center justify-between p-3 bg-gray-50 rounded">
                  <div className="flex items-center space-x-4">
                    <div className="w-8 h-8 bg-blue-500 text-white rounded-full flex items-center justify-center font-bold">
                      {index + 1}
                    </div>
                    <div>
                      <div className="font-semibold">{scorer.playerName}</div>
                    </div>
                  </div>
                  <div className="text-lg font-bold text-blue-600">
                    {scorer.goals} {scorer.goals === 1 ? 'goal' : 'goals'}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Tournament;
